import React, { Fragment, useState } from "react";
//import { output } from "../../../server/controllers/chat_controller";

const ChatOutput = () => {

    const [message, setMessage] = useState('');
    const [response, setResponse] = useState('');
    
    const handleClick = async (e, choice) => {
        e.preventDefault();
        try {
            //const body = response;
            if(choice == 'a'){
            const output = await fetch('http://192.168.1.6:5000/chat/A', {
            //const jsonData = await response.json();
                // headers: { "Content-Type": "application/json"},
                
            })
            }
            else{
                const output = await fetch('http://192.168.1.6:5000/chat/B')
            }
                const jsonData = await output.json();
                JSON.stringify({jsonData});
                setMessage(jsonData);
                // .then((res) => res.json())
                // .then((data) => setResponse(data.message));
                console.log(message.message);
              
        } catch (error) {
            console.error(error.message);
        }
    }
     
    return <Fragment>
    <form className="mt-5" onSubmit={handleClick}>
        {/* <textarea
            value={message.message}
            onChange={(e) => setMessage(e.target.value)}
            >
        </textarea> */}
        <button className="btn col-2 m-3" onClick={() => handleClick('a')}>A</button>
        <button className="btn col-2 m-3" onClick={() => handleClick('b')}>B</button>
        <button className="btn col-2 m-3" onClick={() => handleClick('c')}>C</button>
        <button className="btn col-2 m-3" onClick={() => handleClick('d')}>D</button>
    </form>
    <div className="text-center mt-5" >{message.message}</div> 
    </Fragment>
}

export default ChatOutput
