import React, {Fragment, useState, useEffect} from "react";
import { Link } from "react-router-dom"
const WaitingRoom = ({setPurchase}) => {

    const [concurrentUsers, setConcurrentUsers] = useState(10);
    const [waitTime, setWaitTime] = useState(0);
    

    async function getConcurrentUsers() {
        try {
            const response = await fetch('http://localhost:5000/dashboard',{
                method: "GET",
                headers: {token : localStorage.token}
            });
            const parseResponse = await response.json(); //returns a name
            setConcurrentUsers(parseResponse);
            
        } catch (error) {
            console.error(error.message);
        }
    }
    async function getWaitTime() {
        try {
            const response = await fetch('http://localhost:5000/dashboard',{
                method: "GET",
                headers: {token : localStorage.token}
            });
            const parseResponse = await response.json(); //returns a name
            setWaitTime(parseResponse);
            
        } catch (error) {
            console.error(error.message);
        }
    }
    const logout = (e) => {
        e.preventDefault()
        localStorage.removeItem("token");
        //setAuth(false);
    }
    useEffect(() => {
        
    },[])
    return(
        <Fragment>
            <h1  className="text-center my-5">Waiting Room</h1>
            <h1>Place in line: {concurrentUsers}</h1>
            <h1>Time Remaining: {waitTime}</h1>
            
        </Fragment>
    );
};

export default WaitingRoom;
