import React, { Fragment, useState } from "react";
//import { output } from "../../../server/controllers/chat_controller";

const ChatOutput = () => {

    const [message, setMessage] = useState('');
    const [response, setResponse] = useState('');
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            //const body = response;
            const output = await fetch('http://192.168.1.6:5000/chat', {
            //const jsonData = await response.json();
                // headers: { "Content-Type": "application/json"},
                
            })
                const jsonData = await output.json();
                JSON.stringify({jsonData});
                setMessage(jsonData);
                // .then((res) => res.json())
                // .then((data) => setResponse(data.message));
                console.log(message.message);
              
        } catch (error) {
            console.error(error.message);
        }
    }
     
    return <Fragment>
    <form className="d-flex"onSubmit={handleSubmit}>
        {/* <textarea
            value={message.message}
            onChange={(e) => setMessage(e.target.value)}
            >
        </textarea> */}
        <button className="btn" type="submit">A</button>
        <button className="btn" type="submit">B</button>
        <button className="btn" type="submit">C</button>
        <button className="btn" type="submit">D</button>
    </form>
    <div>{message.message}</div> 
    </Fragment>
}

export default ChatOutput
