import React, {Fragment, useState, useEffect} from "react";
import ChatOutput from './chatOutput';
const waitingRoom = ({setAuth}) => {

    const [concurrentUsers, setConcurrentUsers] = useState(0);
    const [waitTime, setWaitTime] = useState(0);
    

    async function getConcurrentUsers() {
        try {
            const response = await fetch('http://localhost:5000/dashboard',{
                method: "GET",
                headers: {token : localStorage.token}
            });
            const parseResponse = await response.json(); //returns a name
            setConcurrentUsers(parseResponse);
            
        } catch (error) {
            console.error(error.message);
        }
    }

    const logout = (e) => {
        e.preventDefault()
        localStorage.removeItem("token");
        setAuth(false);
    }
    useEffect(() => {
        getName()
    },[])
    return(
        <Fragment>
            <h1>Waiting Room</h1>
            <pre className="text-left mt-5" >{concurrentUsers}</pre>
            <button className="btn btn-primary my-5" onClick={(e) => logout(e)}> Logout </button>
        </Fragment>
    );
};

export default waitingRoom;
