import React, { Fragment, useEffect, useState } from "react";
//import { output } from "../../../server/controllers/chat_controller";

const ChatOutput = () => {

    const [message, setMessage] = useState('');
    //const [response, setResponse] = useState('');
    
    const HandleMulChoiceBut = async (e, choice) => {
        e.preventDefault();
        
        try {
            //const body = response;
            // if(choice == 'a'){
            //     console.log('ran a');
            //     const response = await fetch('http://192.168.1.6:5000/chat/A')
            //     const jsonData = await response.json();
            //     //JSON.stringify({jsonData});
            //     setMessage(jsonData);
            // }
                await fetch('http://192.168.1.6:5000/chat/A',{
                    method: "POST",
                    headers: {
                        'Content-Type' : 'application/json'
                    },
                    body: JSON.stringify( {message: choice} ), //json string, like literally string but json inside. would need to parse to make it usable
                })
                .then((res) => res.json())
                .then((data) => setMessage(data.message));
                //const jsonData = await response.json();
                //JSON.stringify({jsonData});
                //setMessage(jsonData.message);
                // .then((res) => res.json())
                // .then((data) => setResponse(data.message));
                console.log(message);
              
        } catch (error) {
            console.error(error.message);
        }
        useEffect( () => {
            HandleMulChoiceBut();
        }, [])
    }
     
    
    return <Fragment>
    <form className="mt-5" onSubmit={HandleMulChoiceBut}>
        {/* <textarea
            value={message.message}
            onChange={(e) => setMessage(e.target.value)}
            >
        </textarea> */}
        <button className="btn col-2 m-3" onClick={(e) => HandleMulChoiceBut('A')}>A</button>
        <button className="btn col-2 m-3" onClick={() => HandleMulChoiceBut('B')}>B</button>
        <button className="btn col-2 m-3" onClick={() => HandleMulChoiceBut('C')}>C</button>
        <button className="btn col-2 m-3" onClick={() => HandleMulChoiceBut('D')}>D</button>
    </form>
    <pre className="text-left mt-5"  >{message}</pre> 
    </Fragment>
}

export default ChatOutput
