import React, {Fragment, useState} from "react";


const EditTodo = ({eTodo}) => {
    console.log(eTodo);
    const [description, setDescription] = useState(eTodo.description);

    //update description
    const updateDescription = async(e) => {
        e.preventDefault();
        try {
            const body = {description}
            const response = await fetch(`http://66.215.171.251:5000/todos/${eTodo.id}`, {
                method: "PUT",
                headers: { "Content-Type": "application/json"},
                body: JSON.stringify(body)
            });
            console.log(response);
            window.location = "/"
        } catch (error) {
            console.log(error.message);
        }
    };
    return <Fragment>
        {/* <!-- Button to Open the Modal --> */}
        <button 
        type="button" 
        class="btn btn-warning" 
        data-toggle="modal" 
        data-target={`#id${eTodo.id}`}>
        EDIT
        </button>
       
        <div class="modal" id={`id${eTodo.id}`} onClick={() => setDescription(eTodo.description)}>
            <div class="modal-dialog">
                <div class="modal-content">
                    <div class="modal-header">
                        <h4 class="modal-title">Edit Todo</h4>
                        {/* <button type="button" class="close" data-dismiss="modal">&times;</button> */}
                    </div>
                    <div class="modal-body">
                        <input type="text" className="form-control" value={description} onChange={e => setDescription(e.target.value)}/>
                    </div>
                    <div class="modal-footer">
                        <button type="button" class="btn btn-warning" data-dismiss="modal" onClick={e => updateDescription(e)}>Edit</button>
                        <button type="button" class="btn btn-danger" data-dismiss="modal" onClick={() => setDescription(eTodo.description)}>Close</button>
                    </div>
                </div>
            </div>
        </div>
    </Fragment>
};

export default EditTodo;
